import { createContext, useState } from "react";
import axios from "axios";
export const cartContext = createContext()
export default function CartContextProvider({children}){
    const [cart , setCart] = useState(null)
    let headers = {
        token: localStorage.getItem("token"),
      };
      ///////////////////////
async function getCartItems(){
    try{
        let {data} = await axios.get("https://ecommerce.routemisr.com/api/v1/cart", {headers} )
    
    console.log(data)
    }catch(err){
        console.log(err.message)

    }
}
////////////////////////
async function addTocart(productID){
    try{
        let  data  = await axios.post(
            "https://ecommerce.routemisr.com/api/v1/cart",
            { productID },
            { headers }
          );
    }catch(err){
        console.log(err.message)
    }

}
/////////////////////////////////////
async function deleteProduct(productID){
    try{
        let {data} = await axios.delete( `https://ecommerce.routemisr.com/api/v1/cart/${productID}` , {headers})
        console.log(data)

    }catch(err){
        console.log(err.message)
    }

}


    return(
        <>
        <cartContext.Provider value={{cart , setCart ,getCartItems ,addTocart , deleteProduct , }}>
{children}
        </cartContext.Provider>
        
        </>
    )
}
