import React, { useState } from 'react'
import { userSchema } from './Validation/UserValidation';
import axios from 'axios';
export default function Signup() {
const paswordRegx = /^[a-zA-Z0-9!@#$%^&*]{6,16}$/ ;
const maxNum = 10;
const minNum = 6;
const [userName , setUser] = useState("")
const [password , setPassword] = useState("")
const [email , setEmail] = useState("")

  const hanelSubmit = async (e)=>{
    e.preventDefault();
    if(paswordRegx.test(password)){
      const formData = {
        name : userName , 
        email : email ,
        password , password,
        rePassword: password,
        phone: '01290928387',
      }
      // console.log(formData)

  await axios.post("https://ecommerce.routemisr.com/api/v1/auth/signup" , formData)
  .then(({data})=> console.log(data.token)).catch((err)=>{
    console.log(err.response.data.message)
  })
    }else{
      alert("wrong password")
    }
  }
  return (
    <div className='w-100 d-flex justify-content-center align-items-center ' style={{height:"100vh"}}>
<form onSubmit={hanelSubmit} className='form-group w-50'>
  <label htmlFor="useName" className='form-check-label'>userName</label>
  <input type="text" placeholder='userName' className='form-control' id='useName' value={userName} onChange={(e)=>setUser(e.target.value) } />

  <label htmlFor="userEmail" className='form-check-label mt-3'>email</label>
  <input type="email" placeholder='email@email.com' className='form-control' id='userEmail' onChange={(e)=>setEmail(e.target.value) }  value={email}  />

  <label htmlFor="password" className='form-check-label mt-3'>password</label>
  <input type="password" placeholder='password' className='form-control' id='password'  onChange={(e)=>setPassword(e.target.value) } value={password}/>

<input type="submit" className='btn btn-info mt-3' />
</form>
    </div>
  )
}
