import React, { useState } from 'react'
import axios from "axios"
import { Link, Navigate, useNavigate } from 'react-router-dom'
import { createContext } from 'react'
import { AuthContext } from './context/AuthContext'
export default function Login() {
    const [password , setPassword] = useState("")
    const [email , setEmail] = useState("")
    const {setToken} = createContext(AuthContext)
    const navigate = useNavigate()

    const hendleSubmit = async (e)=>{
        e.preventDefault();
        let fromData = {
          email : email ,
          password , password
        }
        console.log(fromData)
        axios.post("https://ecommerce.routemisr.com/api/v1/auth/signin", fromData)
        .then((data)=>{console.log(data)
          localStorage.setItem('token', data.token)
          setToken(data.token)
          navigate("/")
        } ).catch((err)=>{
          console.log(err)
        })
     
        
    }
  return (
    <div className='d-flex justify-content-center align-items-center' style={{height:"100vh"}}> 
<form className='w-50' onSubmit={hendleSubmit}>
    <label htmlFor="email"> email</label>
    <input type="email"className='form-control w-100' placeholder='email'  id='email' required  value={email}  onChange={(e)=> setEmail(e.target.value)}    />
<br />
<label htmlFor="password">password</label>
<input type="password" className='w-100 form-control' placeholder='password' id='password' required value={password} onChange={(e)=> setPassword(e.target.value)}/>

<br />
<p>if you dont have an already account  <Link to={"/signup"}>create account</Link></p>
<button type='submit' className='btn btn-success'>send </button>



</form>






    </div>
  )
}
